import "./App.css";
import Login from "./Components/Login";
import Header from "./Components/Header";
import Register from "./Components/Register";
import TaskForm from "./Components/TaskForm";
import TaskList from "./Components/TaskList";
import SearchForm from "./Components/SearchForm";
import OpenTask from "./Components/OpenTask";
import SortForm from "./Components/SortForm";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useCallback, useEffect, useReducer, useState } from "react";
import AppReducer from "./Reducer/AppReducer";
import ACTION from "./Reducer/Action";
import axios from "axios";

const getIndex = (tasks, id) => {
  let i = -1;
  tasks.forEach((task, index) => {
    if (task._id === id) {
      i = index;
    }
  });
  return i;
};
function App() {
  const initialState = { user: null, tasks: [] };
  const [state, dispatch] = useReducer(AppReducer, initialState);
  const [openTaskForm, setOpenTaskForm] = useState(false);
  const [updateTask, setUpdateTask] = useState({
    _id: "",
    task: "",
    status: false,
  });
  const closeForm = () => {
    setOpenTaskForm(false);
  };
  const toggleTaskForm = () => {
    setOpenTaskForm(!openTaskForm);
  };
  const setOpenForm = () => {
    toggleTaskForm();
    setUpdateTask({ _id: "", task: "", status: false });
  };
  useEffect(
    useCallback(async () => {
      try {
        const token = localStorage.getItem("token");
        const option = {
          method: "get",
          url: "/api/v1/auth",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
        const response = await axios(option);
        const { userName } = response.data.data.user;
        dispatch({ type: "CURRENT_USER", payload: userName });
      } catch (error) {}
    }, [dispatch]),
    []
  );
  const getTasks = useCallback(async () => {
    const option = {
      method: "get",
      url: "/api/v1/task",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    };
    const response = await axios(option);
    const taskList = response.data.data;
    dispatch({ type: "GET_USER_TASKS", payload: taskList });
  }, [dispatch]);

  useEffect(() => {
    getTasks();
  }, [getTasks]);

  const userName = (name) => {
    dispatch({ type: "CURRENT_USER", payload: name });
  };

  const signOut = () => {
    dispatch({ type: "CURRENT_USER", payload: null });
  };

  const updateStatus = async (id) => {
    try {
      const index = getIndex(state.tasks, id);
      const { status } = state.tasks[index];
      const option = {
        method: "put",
        url: `/api/v1/task/${id}`,
        data: {
          status: !status,
        },
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      };
      await axios(option);
      dispatch({
        type: "UPDATE_TASK",
        payload: { ...state.tasks[index], status: !status },
      });
    } catch (error) {
      console.log(error);
    }
  };

  const onUpdateTask = (id) => {
    const index = getIndex(state.tasks, id);
    const task = state.tasks[index];
    setUpdateTask({ _id: task._id, task: task.task, status: task.status });
    id === updateTask._id ? toggleTaskForm() : setOpenTaskForm(true);
  };

  const onSetData = (tasks) => {
    if (task._id === "") {
      try {
        const option = {
          method: "post",
          url: "/api/v1/task",
        };
        const { task, status } = tasks;
        console.log({ task: task, status: status });
      } catch (error) {
        console.log(error);
      }
    } else {
    }
  };

  return (
    <Router>
      {state.user ? <Header name={state.user} setSignOut={signOut} /> : ""}
      <Switch>
        <Route exact path="/login">
          <Login getName={userName} />
        </Route>
        <Route exact path="/register">
          <Register />
        </Route>
        <Route exact path="/">
          <div className="header">
            <div className="container">
              <h1 className="title">Quản lí công việc</h1>
            </div>
          </div>
          <div className="content">
            <div className="container">
              {openTaskForm ? (
                <TaskForm
                  closeForm={closeForm}
                  taskUpdate={updateTask}
                  setData={onSetData}
                />
              ) : (
                ""
              )}
              <div
                className={openTaskForm ? "task-list" : "task-list with-100"}
              >
                <OpenTask
                  openTaskForm={setOpenForm}
                  openStatus={openTaskForm}
                />
                <div className="form-search">
                  <SearchForm />
                  <SortForm />
                </div>
                <TaskList
                  tasks={state.tasks}
                  updateStatus={updateStatus}
                  onUpdateTask={onUpdateTask}
                />
              </div>
            </div>
          </div>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
