import React, { useState } from 'react';


function Pagination(props) {
    const {pagination} = props;
    const [pagi, setPagi] = useState(pagination);
    const element = [];
    for(let i = 1; i <= Math.ceil(pagi.total/pagi.limit); i++){
        element.push(<li>{i}</li>);
    }
    return (
            <ul className = "pagination">
                <li className = {(pagi.page <= 1 ? "enable" : "")}>Pre</li>
                <li className = "not-active">1</li>
                {element}
                <li className = {(pagi.page >= (Math.ceil(pagi.total/pagi.limit) ) ? "enable" : "")}>Next</li>
            </ul>
    );
}

export default Pagination;
