import React, { useState } from 'react';
import PaginationItem from './PaginationItem';


function Pagination(props) {
    const {pagination, getPagination} = props;
    const [pagi, setPagi] = useState(pagination);
    const element = [];
    const getPageIndex = (index) => {
        setPagi({...pagi, page: index});
        getPagination(pagi);
    }
    for(let i = 1; i <= Math.ceil(pagi.total/pagi.limit); i++){
        element.push(
            <PaginationItem 
                key = {i}
                pageIndex = {i}
                isChecked = {i === pagi.page ? true : false}
                getPageIndex = {getPageIndex}
                ></PaginationItem>
        );
    }
    const onHandlePre = () => {
        setPagi({...pagi, page : pagi.page - 1});
    };
    const onHandleNext = () => {
        setPagi({...pagi, page : pagi.page + 1});
    }
    return (
            <ul className = "pagination">
                <li className = {(pagi.page <= 1 ? "enable" : "")} onClick = {onHandlePre}>Pre</li>
                {element}
                <li className = {(pagi.page >= (Math.ceil(pagi.total/pagi.limit) ) ? "enable" : "")} onClick = {onHandleNext}>Next</li>
            </ul>
    );
}

export default React.memo(Pagination) ;
