import ACTION from "./Action";

export default function reducer(state, action) {
  switch (action.type) {
    case ACTION.GET_USER_TASKS:
      return { ...state, tasks: action.payload };
    case ACTION.CURRENT_USER:
      return { ...state, user: action.payload };

    default:
      return state;
  }
}
